package dsa;

public class Introarrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] marks = new int[5];
		marks[0]=20;
		marks[1]=30;
		marks[2]=40;
		marks[3]=50;
		marks[4]=60;
		for(int i=0;i<marks.length;i++) {
			System.out.println(i+" "+marks[i]);
		}
	}

}
